import Modal from "react-modal";

export function PizzaDetails({ pizza, selectedSize, setSelectedSize }) {
  return (
    <div className="pizza-details">
      <h2>{pizza.name}</h2>
      <img src={pizza.img} alt={pizza.name} className="pizza-image" />
      <p className="pizza-price">Price: {pizza.price}</p>
      <p>{pizza.description}</p>
      <div className="size-selection">
        {pizza.size.map((size) => (
          <button
            key={size}
            onClick={() => setSelectedSize(size)}
            className={`size-button ${selectedSize === size ? "selected" : ""}`}
          >
            {size}
          </button>
        ))}
      </div>
    </div>
  );
}

export function QuantityControls({ quantity, setQuantity }) {
  return (
    <div className="quantity-controls">
      <button
        onClick={() => setQuantity(quantity > 1 ? quantity - 1 : 1)}
        className="quantity-button"
      >
        -
      </button>
      <span className="quantity-display">{quantity}</span>
      <button
        onClick={() => setQuantity(quantity + 1)}
        className="quantity-button"
      >
        +
      </button>
    </div>
  );
}

export function AddToCartButton() {
  return <button className="add-to-cart-button">Add to Cart</button>;
}

export default function PizzaModal({
  isOpen,
  onRequestClose,
  selectedPizza,
  quantity,
  setQuantity,
  selectedSize,
  setSelectedSize,
}) {
  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onRequestClose}
      contentLabel="Pizza Details"
    >
      {selectedPizza && (
        <>
          <PizzaDetails
            pizza={selectedPizza}
            selectedSize={selectedSize}
            setSelectedSize={setSelectedSize}
          />
          <div className="control-container">
            <QuantityControls quantity={quantity} setQuantity={setQuantity} />
            <AddToCartButton />
          </div>
        </>
      )}
    </Modal>
  );
}
