import React, { useState } from "react";
import Modal from "react-modal";
import Method from "../Data/Method"; 
import "../Styles/Main.css";
import "../Styles/Success.css";
import { Link } from "react-router-dom";

// Required for react-modal accessibility
Modal.setAppElement("#root");

export default function SuccessPage() {
  const [modalIsOpen, setModalIsOpen] = useState(false);

  // Functions to handle modal open and close
  const openModal = () => {
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  return (
    <div className="header_success">
      <div className="conten">
        <h2>ORDER SUCCESSFUL!</h2>
      </div>

      {/* Display all images below the success message */}
      <div className="image_gallery">
        {Method.map((item) => (
          <img key={item.idSp} src={item.img} alt={item.name || "Method Image"} className="method_img" />
        ))}
      </div>

      <div className="button">
        <button onClick={openModal}>Tracking your order</button>
        <Link to="/">
          <button>Keep Ordering</button>
        </Link>
      </div>

      {/* Modal for success message */}
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="Order Success Modal"
        className="modal_content"
        overlayClassName="modal_overlay"
      >
        <h2>Your Order has been placed!</h2>
        <button onClick={closeModal} className="close_modal">Close</button>

        {/* Display images inside the modal */}
        <div className="image_gallery_modal">
          {Method.map((item) => (
            <img key={item.idSp} src={item.img} alt={item.name || "Method Image"} className="method_img_modal" />
          ))}
        </div>
      </Modal>
    </div>
  );
}
