import React from "react";
import Modal from "react-modal";

import "../Styles/Bill.css";

export default function OrderBill({ isOpen, onRequestClose }) {
  // Assuming pizza data is stored in localStorage
  const pizzaData = JSON.parse(localStorage.getItem("bill")) || [];

  const calculateTotal = () => {
    return pizzaData.reduce(
      (total, pizza) => total + pizza.totalPrice * pizza.quantity,
      0
    );
  };

  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onRequestClose}
      contentLabel="Order Bill"
      className="modal-content"
      overlayClassName="modal-overlay"
    >
      <div className="order-container">
        <div className="header">
          <span className="store-name">Order History</span>
        </div>
        <div className="order-details">
          {pizzaData.length > 0 ? (
            pizzaData.map((pizza) => (
              <div className="product-info" key={pizza.id}>
                <p className="product-title">
                  {pizza.name} ({pizza.size})
                </p>
                <p className="product-desc">
                  Ngày đặt hàng: {new Date(pizza.id).toLocaleString()}
                </p>
                <p className="product-desc">Số lượng: {pizza.quantity}</p>
                <p className="product-desc">Giá: {pizza.totalPrice} VNĐ</p>
              </div>
            ))
          ) : (
            <p>No orders available</p>
          )}
        </div>
        <div className="total-section">
          <p className="total-text">Tổng số tiền: {calculateTotal()} VNĐ</p>
        </div>
      </div>
    </Modal>
  );
}
