import Modal from "react-modal";
import Pizza from "../Data/Pizza";
import "../Styles/Bill.css";
import { useState, useEffect } from "react";

export default function OrderBill({ isOpen, onRequestClose }) {
  const [bills, setBills] = useState([]);

  useEffect(() => {
    try {
      const storedData = localStorage.getItem("bill");
      if (storedData) {
        const storedBills = JSON.parse(storedData);
        if (Array.isArray(storedBills)) {
          const sortedBills = storedBills.sort(
            (a, b) => new Date(b.dateTime) - new Date(a.dateTime) // Sắp xếp theo thời gian
          );
          setBills(sortedBills);
        } else {
          console.error("Dữ liệu không phải là một mảng.");
          setBills([]); // Đặt giá trị mặc định nếu dữ liệu không hợp lệ
        }
      } else {
        console.warn("Không có dữ liệu nào trong localStorage.");
        setBills([]);
      }
    } catch (error) {
      console.error("Lỗi khi parse dữ liệu từ localStorage:", error);
      setBills([]); // Đặt giá trị mặc định nếu xảy ra lỗi
    }
  }, [isOpen]);

  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onRequestClose}
      contentLabel="Order Bill"
      className="modal-content"
      overlayClassName="modal-overlay"
    >
      <div className="order-container">
        <div className="header">
          <span className="store-name">Order History</span>
        </div>
        {bills.map((bill, index) => (
          <div key={bill.id} className="bill-section">
            <h4>Bill #{index + 1}</h4>
            <p className="product-desc">Ngày đặt hàng: {bill.id}</p>
            <div className="order-details">
              {bill.products.map((product, idx) => (
                <div key={idx} className="product-info">
                  <p className="product-title">{product.name}</p>
                  <p>Kích thước: {product.size}</p>
                  <p>Giá: {product.price.toLocaleString()} VND</p>
                  <p>Số lượng: {product.quantity}</p>
                  <p>Tổng tiền: {product.totalPrice.toLocaleString()} VND</p>
                </div>
              ))}
            </div>
          </div>
        ))}
        <div className="total-section">
          <p className="total-text">
            Tổng số tiền:{" "}
            {bills
              .reduce(
                (total, bill) =>
                  total +
                  bill.products.reduce(
                    (subtotal, product) => subtotal + product.total,
                    0
                  ),
                0
              )
              .toLocaleString()}{" "}
            VND
          </p>
        </div>
      </div>
    </Modal>
  );
}
