import React, { useState, useEffect } from "react";
import Modal from "react-modal";
import "../Styles/Bill.css";

export default function OrderBill({ isOpen, onRequestClose }) {
  const [bills, setBills] = useState([]);

  useEffect(() => {
    // const getBills = JSON.parse(localStorage.getItem("bill"));
    setBills([
      {
        id: "2024-11-26T11:23:54.228Z",
        name: "Pizza Ngũ Hành",
        size: "Small",
        price: 18,
        quantity: 1,
      },
    ]);
  }, []);

  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onRequestClose}
      contentLabel="Order Bill"
      className="modal-content"
      overlayClassName="modal-overlay"
    >
      <div className="order-container">
        <div className="header">
          <span className="store-name">Order History</span>
        </div>
        <div className="order-details">
          <table className="product-table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Username</th>
                <th>Address</th>
                <th>Product</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Total Price</th>
              </tr>
            </thead>
            <tbody>
              {bills.map((bill, idx) => (
                <tr key={idx} className="product-info no-border">
                  <td>{bill.id}</td>
                  <td>{bill.username}</td>
                  <td>{bill.address}</td>
                  <td>{bill.name}</td>
                  <td>${bill.price}</td>
                  <td>x{bill.quantity}</td>
                  <td>${bill.totalPrice}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </Modal>
  );
}
