import React, { useState } from "react";
import "../Styles/Login.css";
import Users from "../Data/users"; // Importing the users array

export default function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault(); // Prevent the default form submission behavior

    // Check if the entered username and password match any user in the users array
    const user = Users.find(
      (user) => user.username === username && user.password === password
    );

    if (user) {
      // Login successful
      setErrorMessage(""); // Clear any previous error message
      alert("Login successful!");
      // You can navigate to another page or do any further action after login success
    } else {
      // Login failed
      alert("Fail");
    }
  };

  return (
    <div className="center">
      <h1>Login</h1>
      <form method="post" onSubmit={handleSubmit}>
        {/* Username input */}
        <div className="txt_field">
          <input
            type="text"
            required
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <span></span>
          <label>Username</label>
        </div>

        {/* Password input */}
        <div className="txt_field">
          <input
            type="password"
            required
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <span></span>
          <label>Password</label>
        </div>

        {/* Display error message if login fails */}
        {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}

        {/* Forgot password link */}
        <div className="pass">Forgot Password?</div>

        {/* Submit button */}
        <input type="submit" value="Login" onClick={handleSubmit} />

        {/* Sign-up link */}
        <div className="signup_link">
          Not a member?
          <a href="#">Sign up</a>
        </div>
      </form>
    </div>
  );
}
