import React from "react";
import Modal from "react-modal";
import "../Styles/Bill.css";

// Sample data (normally this would come from your client side storage)
const bills = [
  {
    id: "2024-11-28T04:44:36.197Z",
    name: "Pizza Pesto",
    size: "Small",
    price: 24,
    quantity: 4,
    totalPrice: 96,
    address: "P.13",
    username: "phuc",
  },
  {
    id: "2024-11-28T04:44:36.197Z",
    name: "Pizza Gà",
    size: "Small",
    price: 24,
    quantity: 10,
    totalPrice: 240,
    address: "P.13",
    username: "phuc",
  },
  // Add more bills as needed
];

// Group bills by order date
const groupBillsByDate = (bills) => {
  return bills.reduce((groups, bill) => {
    const date = new Date(bill.id).toLocaleDateString();
    if (!groups[date]) {
      groups[date] = [];
    }
    groups[date].push(bill);
    return groups;
  }, {});
};

const groupedBills = groupBillsByDate(bills);

export default function OrderBill({ isOpen, onRequestClose }) {
  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onRequestClose}
      contentLabel="Order Bill"
      className="modal-content"
      overlayClassName="modal-overlay"
    >
      <div className="order-container">
        <div className="header">
          <span className="store-name">Order History</span>
        </div>
        <div className="order-details">
          {Object.keys(groupedBills).map((date, index) => {
            const total = groupedBills[date].reduce(
              (sum, bill) => sum + bill.totalPrice,
              0
            );
            const representativeBill = groupedBills[date][0];
            return (
              <div key={index} className="date-group">
                <div className="date">{date}</div>
                <div className="common-info">
                  <div>Name: {representativeBill.username}</div>
                  <div>Address: {representativeBill.address}</div>
                </div>
                <table className="product-table">
                  <thead>
                    <tr>
                      <th>Product</th>
                      <th>Price</th>
                      <th>Quantity</th>
                      <th>Total Price</th>
                    </tr>
                  </thead>
                  <tbody>
                    {groupedBills[date].map((bill, idx) => (
                      <tr key={idx} className="product-info no-border">
                        <td>{bill.name}</td>
                        <td>{bill.price}</td>
                        <td>{bill.quantity}</td>
                        <td>{bill.totalPrice}</td>
                      </tr>
                    ))}
                    <tr>
                      <td colSpan="3" className="text-right">
                        <strong>Total</strong>
                      </td>
                      <td>
                        <strong>${total}</strong>
                      </td>
                    </tr>
                  </tbody>
                </table>
              </div>
            );
          })}
        </div>
      </div>
    </Modal>
  );
}
