import Modal from "react-modal";
import "../Styles/Bill.css";
import { useState, useEffect } from "react";

export default function OrderBill({ isOpen, onRequestClose }) {
  const [groupedBills, setGroupedBills] = useState([]);

  useEffect(() => {
    try {
      const storedData = localStorage.getItem("bill");
      if (storedData) {
        const bills = JSON.parse(storedData);

        if (Array.isArray(bills)) {
          // Nhóm sản phẩm theo `id` (datetime)
          const grouped = bills.reduce((acc, bill) => {
            if (!acc[bill.id]) {
              acc[bill.id] = [];
            }
            acc[bill.id].push(bill);
            return acc;
          }, {});

          // Chuyển từ object sang mảng, và sắp xếp theo thời gian
          const sortedGroupedBills = Object.entries(grouped)
            .map(([id, products]) => ({
              id,
              products,
            }))
            .sort((a, b) => new Date(b.id) - new Date(a.id)); // Sắp xếp theo thời gian

          setGroupedBills(sortedGroupedBills);
        } else {
          console.error("Dữ liệu không phải là một mảng.");
          setGroupedBills([]);
        }
      } else {
        console.warn("Không có dữ liệu nào trong localStorage.");
        setGroupedBills([]);
      }
    } catch (error) {
      console.error("Lỗi khi parse dữ liệu từ localStorage:", error);
      setGroupedBills([]);
    }
  }, [isOpen]);

  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onRequestClose}
      contentLabel="Order Bill"
      className="modal-content"
      overlayClassName="modal-overlay"
    >
      <div className="order-container">
        <div className="header">
          <span className="store-name">Order History</span>
        </div>
        {groupedBills.map((bill, index) => (
          <div key={bill.id} className="bill-section">
            <h4>
              Bill #{index + 1} - Ngày đặt: {new Date(bill.id).toLocaleString()}
            </h4>
            <div className="order-details">
              {bill.map((product, idx) => (
                <div key={idx} className="product-info">
                  <p className="product-title">{product.name}</p>
                  <p>Kích thước: {product.size}</p>
                  <p>Giá: {product.price.toLocaleString()} VND</p>
                  <p>Số lượng: {product.quantity}</p>
                  <p>Tổng tiền: {product.totalPrice.toLocaleString()} VND</p>
                </div>
              ))}
            </div>
          </div>
        ))}
        <div className="total-section">
          <p className="total-text">
            Tổng số tiền:{" "}
            {groupedBills
              .reduce(
                (total, bill) =>
                  total +
                  bill.products.reduce(
                    (subtotal, product) => subtotal + product.totalPrice,
                    0
                  ),
                0
              )
              .toLocaleString()}{" "}
            VND
          </p>
        </div>
      </div>
    </Modal>
  );
}
