import React, { useState } from "react";
import { PaymentPage } from "./PaymentPage"; // Import your payment page component

export default function Homepage() {
  const [showPayment, setShowPayment] = useState(false); // State to handle payment visibility

  const handleCartClick = () => {
    setShowPayment(true); // Show the payment page when cart button is clicked
  };

  return (
    <div className="homepage">
      {/* Your existing homepage components */}

      <button onClick={handleCartClick}>Cart</button> {/* Cart button */}
      
      {showPayment && <PaymentPage />} {/* Conditionally render payment page */}
    </div>
  );
}
