import Modal from "react-modal";
import "../Styles/Bill.css";

export default function OrderBill({ isOpen, onRequestClose }) {
  // Calculate total price
  const totalPrice = orderItems.reduce((sum, item) => sum + item.total, 0);

  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onRequestClose}
      contentLabel="Order Bill"
      className="modal-content"
      overlayClassName="modal-overlay"
    >
      <div className="order-container">
        <div className="header">
          <span className="store-name">Order History</span>
        </div>
        <div className="order-details">
          {"Dùng map() để lập và lấy ra các thuộc tính của bill".map(
            (item, index) => (
              <div key={index} className="product-info">
                {/* name */}
                {/* address */}
                {/* tên sản phẩm */}
                {/* Giá */}
                {/* Số lượng */}
                {/* Tổng tiền của sản phẩm này */}
                {/* Ngày đặt */}

                {/* Dựa vào ngày đặt từ "bill" nếu mà có 2 hoặc nhiều sản phẩm trùng ngày đặt thì phân loại nó thành 1 khung , như liệu mẫu như sau */}
                {/* {/* name và address là thông tin chung cho cả 2 hoặc nhiều bill */}
                {/* tên sản phẩm 1 */}
                {/* Giá của sản phẩm 1*/}
                {/* Số lượng của sản phẩm 1 */}
                {/* Tổng tiền của sản phẩm 1 */}
                {/* ---- */}
                {/* tên sản phẩm 2 */}
                {/* Giá của sản phẩm 2*/}
                {/* Số lượng của sản phẩm 2 */}
                {/* Tổng tiền của sản phẩm 2 */}
                {/* Ghi tổng tiền của 2 sản phẩm trên */}
              </div>
            )
          )}
        </div>
      </div>
    </Modal>
  );
}
