import React, { useState, useEffect } from "react";
import Modal from "react-modal";
import "../Styles/Bill.css";

// Group bills by order date
const groupBillsByDate = (bills) => {
  return bills.reduce((groups, bill) => {
    const date = new Date(bill.id).toLocaleDateString();
    if (!groups[date]) {
      groups[date] = [];
    }
    groups[date].push(bill);
    return groups;
  }, {});
};

export default function OrderBill({ isOpen, onRequestClose }) {
  const [bills, setBills] = useState([]);

  useEffect(() => {
    const getBills = JSON.parse(localStorage.getItem("bill")) || [];
    setBills(getBills);
  }, []);

  // const groupedBills = groupBillsByDate(bills);

  return (
    <div>
      {bills.map((item) => (
        <>
          <h1>{item.name}</h1>
          <h1>{item.name}</h1>
          <h1>{item.name}</h1>
          <h1>{item.name}</h1>
        </>
      ))}
    </div>
  );
}
