import "@fortawesome/fontawesome-free/css/all.min.css";
import React, { useEffect } from "react";
import "../Styles/Payment.css";
import "../Styles/Main.css";
import { Link } from "react-router-dom";
import { useState } from "react";

//Chứa các thành phần thanh toán

const Cart = () => {
  const [cart, setCart] = useState([]);

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
  }, []);

  const handleQuantityChange = (id, action) => {
    const updatedCart = cart.map((item) => {
      if (item.id === id) {
        const newQuantity =
          action === "increase" ? item.quantity + 1 : item.quantity - 1;

        if (newQuantity >= 0) {
          return {
            ...item,
            quantity: newQuantity,
            totalPrice: newQuantity * item.price,
          };
        } else {
          return item;
        }
      } else {
        return item;
      }
    });

    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  const handleRemoveItem = (id) => {
    const updatedCart = cart.filter((item) => item.id !== id);

    const reindexedCart = updatedCart.map((item, index) => ({
      ...item,
      id: index + 1,
    }));

    let currentCounter = parseInt(localStorage.getItem("counter"), 10);
    currentCounter = currentCounter > 0 ? currentCounter - 1 : 0;

    localStorage.setItem("counter", currentCounter);

    setCart(reindexedCart);
    localStorage.setItem("cart", JSON.stringify(reindexedCart));
  };

  // Tính tổng giá trị của giỏ hàng
  const totalCartValue = cart.reduce(
    (total, item) => total + item.quantity * item.price,
    0
  );

  return (
    <div className="cart-table">
      <h2>Your Cart</h2>
      {cart.length === 0 ? (
        <p>Cart is empty</p>
      ) : (
        <div>
          <table className="cart-item-table">
            <thead>
              <tr>
                <th>ID</th>
                <th>Name</th>
                <th>Price</th>
                <th>Quantity</th>
                <th>Total</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {cart.map((item) => (
                <tr key={item.id}>
                  <td>{item.id}</td>
                  <td>{item.name}</td>
                  <td>${item.price.toFixed(2)}</td>
                  <td>
                    <button
                      className="quantity-btn"
                      onClick={() => handleQuantityChange(item.id, "decrease")}
                      disabled={item.quantity === 1}
                    >
                      -
                    </button>
                    {item.quantity}
                    <button
                      className="quantity-btn"
                      onClick={() => handleQuantityChange(item.id, "increase")}
                    >
                      +
                    </button>
                  </td>
                  <td>${(item.quantity * item.price).toFixed(2)}</td>
                  <td>
                    <button
                      className="remove-btn"
                      onClick={() => handleRemoveItem(item.id)}
                    >
                      Remove
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          {/* Dòng tổng giá trị */}
          <div className="cart-total">
            <h3>Total: ${totalCartValue.toFixed(2)}</h3>
          </div>
        </div>
      )}
    </div>
  );
};
