import "@fortawesome/fontawesome-free/css/all.min.css";
import React, { useEffect } from "react";
import "../Styles/Payment.css";
import "../Styles/Main.css";
import { Link } from "react-router-dom";
import { useState } from "react";

const Cart = () => {
  const [cart, setCart] = useState([]);

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
  }, []);

  const handleQuantityChange = (id, action) => {
    const updatedCart = cart.map((item) => {
      if (item.id === id) {
        const newQuantity =
          action === "increase" ? item.quantity + 1 : item.quantity - 1;

        // Đảm bảo số lượng không âm
        if (newQuantity >= 0) {
          return {
            ...item,
            quantity: newQuantity,
            totalPrice: newQuantity * item.price,
          };
        } else {
          return item; // Giữ nguyên nếu số lượng giảm xuống dưới 0
        }
      } else {
        return item; // Giữ nguyên các sản phẩm khác
      }
    });

    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  const handleRemoveItem = (id) => {
    // Reset a counter (e.g., a global or local counter variable)
    let counter = 1; // Reset counter

    // Filter out the item with the specified id
    const updatedCart = cart.filter((item) => item.id !== id);

    if (updatedCart.length === 0) {
      // Reset cart and counter if the cart is empty
      setCart([]);
      localStorage.setItem("cart", JSON.stringify([]));
    } else {
      // Reindex the remaining items and reset their counters
      const reindexedCart = updatedCart.map((item, index) => {
        // Reset each item's id starting from 1
        return {
          ...item,
          id: index + 1,
          counter: counter++, // Reset and increment counter
        };
      });

      // Update the cart and save to localStorage
      setCart(reindexedCart);
      localStorage.setItem("cart", JSON.stringify(reindexedCart));
    }

    console.log("Counter reset to:", counter);
  };

  return (
    <div className="cart-table">
      <h2>Your Cart</h2>
      {cart.length === 0 ? (
        <p>Cart is empty</p>
      ) : (
        <table className="cart-item-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Price</th>
              <th>Quantity</th>
              <th>Total</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {cart.map((item) => (
              <tr key={item.id}>
                <td>{item.id}</td>
                <td>{item.name}</td>
                <td>${item.price.toFixed(2)}</td>
                <td>
                  <button
                    className="quantity-btn"
                    onClick={() => handleQuantityChange(item.id, "decrease")}
                    disabled={item.quantity === 1} // Disable decrease button if quantity is 1
                  >
                    -
                  </button>
                  {item.quantity}
                  <button
                    className="quantity-btn"
                    onClick={() => handleQuantityChange(item.id, "increase")}
                  >
                    +
                  </button>
                </td>
                <td>${(item.quantity * item.price).toFixed(2)}</td>
                <td>
                  <button
                    className="remove-btn"
                    onClick={() => handleRemoveItem(item.id)}
                  >
                    Remove
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

//Chứa các thành phần thanh toán
export function PaymentInfo() {
  return (
    <div>
      <FillUserInfo />
      <FillPaymentMethod />
      <FillNote />
      <ConfirmPayment />
    </div>
  );
}

//Thành phần điền thông tin người dùng
export function FillUserInfo() {
  return (
    <div className="user-info">
      <div className="fill-infor">
        <h1 className="title">FILL YOUR INFORMATION</h1>
        <form className="user-form">
          <input
            type="text"
            id="name"
            name="name"
            placeholder="Name"
            style={{
              backgroundColor: "#d2cdcd53",
              width: "600px",
              height: "35px",
            }}
          />
          <br />
          <input
            type="text"
            id="phonenumber"
            name="phonenumber"
            placeholder="Phone Number"
            style={{
              backgroundColor: "#d2cdcd53",
              width: "600px",
              height: "35px",
            }}
          />
          <br />
          <input
            type="text"
            name="address"
            id="address"
            placeholder="Address"
            style={{
              backgroundColor: "#d2cdcd53",
              width: "600px",
              height: "35px",
            }}
          />
          <br />
        </form>
      </div>
