import React, { useState } from "react";
import Modal from "react-modal";
import Method from "../Data/Method"; 
import "../Styles/Main.css";
import "../Styles/Success.css";
import { Link } from "react-router-dom";

// Required for react-modal accessibility
Modal.setAppElement("#root");

export default function SuccessPage() {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [selectedImage, setSelectedImage] = useState("");

  // Functions to handle modal open and close
  const openModal = (img) => {
    setSelectedImage(img);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setSelectedImage("");
  };

  // Get the banner image
  const bannerImage = Method.find((item) => item.img === "/img/banner.png");

  return (
    <div className="header_success">
      <div className="conten">
        <h2>ORDER SUCCESSFUL!</h2>
      </div>

      {/* Display the banner image */}
      <div className="banner_image">
        {bannerImage ? (
          <img
            src={bannerImage.img}
            alt="Banner"
            className="banner_img"
            onClick={() => openModal(bannerImage.img)} // Open modal on banner click
          />
        ) : (
          <p>No banner image available</p>
        )}
      </div>

      <div className="button">
        <button onClick={() => openModal(bannerImage.img)}>Tracking your order</button>
        <Link to="/">
          <button>Keep Ordering</button>
        </Link>
      </div>

      {/* Display the images from Method */}
      <div className="images_container">
        {Method.map((item) => (
          <img
            key={item.idSp}
            src={item.img}
            alt={item.name || "Image"}
            className="method_image"
            onClick={() => openModal(item.img)} // Open modal on image click
          />
        ))}
      </div>

      {/* Modal for displaying the selected image */}
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="Image Modal"
        className="modal_content"
        overlayClassName="modal_overlay"
      >
        <img src={selectedImage} alt="Selected" className="modal_image" />
        <button onClick={closeModal} className="close_modal">Close</button>
      </Modal>
    </div>
  );
}
