import "@fortawesome/fontawesome-free/css/all.min.css";
import "../Styles/HomePage.css";

const products = [
  {
    img: "/img/1.png",
    overlayImg: "/img/Ham-topping.png",
    name: "Product 1",
    price: "$25.00",
  },
  { img: "/img/2.png", name: "Product 2", price: "$35.00" },
  { img: "/img/3.png", name: "Product 3", price: "$45.00" },
  { img: "/img/4.png", name: "Product 4", price: "$45.00" },
  { img: "/img/5.png", name: "Product 5", price: "$45.00" },
  { img: "/img/6.png", name: "Product 6", price: "$45.00" },
  { img: "/img/7.png", name: "Product 7", price: "$45.00" },
  { img: "/img/8.png", name: "Product 8", price: "$45.00" },
  { img: "/img/9.png", name: "Product 9", price: "$45.00" },
  { img: "/img/10.png", name: "Product 10", price: "$45.00" },
  { img: "/img/11.png", name: "Product 11", price: "$45.00" },
  { img: "/img/12.png", name: "Product 12", price: "$45.00" },
];

export default function HomePage() {
  return (
    <div>
      <header>
        <div>
          <h1>OUR SIGNATURE</h1>
        </div>
        <div>
          <a>
            <i className="fas fa-user"></i>
          </a>
          <a>
            <i className="fas fa-shopping-cart"></i>
          </a>
        </div>
      </header>
      <main>
        <div style={{ display: "flex", gap: "20px", flexWrap: "wrap" }}>
          {products.map((product, index) => (
            <div key={index} className="product-container">
              <img
                src={product.img}
                style={{ width: "150px", height: "150px" }}
                alt={product.name}
              />
              <h3>{product.name}</h3>
              <p>Price: {product.price}</p>
              {product.overlayImg && (
                <img
                  src={product.overlayImg}
                  className="overlay-image"
                  alt={`${product.name} overlay`}
                />
              )}
            </div>
          ))}
        </div>
      </main>
    </div>
  );
}
