export function ConfirmPayment({ isFormValid }) {
  const handlePayment = (e) => {
    if (!isFormValid()) {
      e.preventDefault();
      alert("Vui lòng điền thông tin");
      return;
    }

    // Get the cart from localStorage (or from state if using useState)
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];

    // Get the payment details (you can include more data as needed)
    const bill = {
      bill: storedCart,
    };

    // Save the bill data in localStorage
    localStorage.setItem("bill", JSON.stringify(bill));

    // Optionally, you can navigate to the success page here:
    // window.location.href = '/successpage';
  };

  return (
    <div className="confirm-payment">
      <Link to="/successpage">
        <button
          className="btn-confirm"
          onClick={handlePayment} // Call the handlePayment function on click
        >
          PAY
        </button>
      </Link>
      <Link to="/" style={{ textDecoration: "underline" }}>
        Add more ? ...
      </Link>
    </div>
  );
}

