import React, { useEffect, useState } from "react";
import Modal from "react-modal";
import "../Styles/Bill.css";

export default function OrderBill({ isOpen, onRequestClose }) {
  const [groupedData, setGroupedData] = useState([]);
  const [totalPrice_bill, setTotalPrice] = useState(0);

  useEffect(() => {
    // Đọc dữ liệu từ localStorage
    const storedData = JSON.parse(localStorage.getItem("bill")) || [];

    // Kiểm tra xem storedData có phải là mảng không
    if (Array.isArray(storedData)) {
      // Nhóm dữ liệu theo id
      const grouped = storedData.reduce((acc, item) => {
        if (!acc[item.id]) {
          acc[item.id] = { ...item, quantity: 0, total: 0 };
        }
        acc[item.id].quantity += item.quantity;
        acc[item.id].total += item.totalPrice * item.quantity;
        return acc;
      }, {});

      const groupedArray = Object.values(grouped);

      // Tính tổng tiền
      const total = groupedArray.reduce((sum, item) => sum + item.total, 0);

      setGroupedData(groupedArray);
      setTotalPrice(total);
    } else {
      console.error("Dữ liệu trong localStorage không phải là mảng");
    }
  }, [isOpen]);

  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onRequestClose}
      contentLabel="Order Bill"
      className="modal-content"
      overlayClassName="modal-overlay"
    >
      <div className="order-container">
        <div className="header">
          <span className="store-name">Order History</span>
        </div>
        <div className="order-details">
          {groupedData.map((item) => (
            <div key={item.id} className="product-info">
              <p className="product-title">{item.name}</p>
              <p className="product-desc">
                Kích thước: {item.size} | Số lượng: {item.quantity}
              </p>
              <p className="price-info">Giá: {item.total} đ</p>
            </div>
          ))}
        </div>
        <div className="total-section">
          <p className="total-text">Tổng số tiền: {totalPrice_bill} đ</p>
        </div>
      </div>
    </Modal>
  );
}
