import React, { useState, useEffect } from "react";
import Modal from "react-modal";
import "../Styles/Bill.css";

export default function OrderBill({ isOpen, onRequestClose }) {
  const [bill, setBill] = useState([]);

  // Lấy dữ liệu từ localStorage khi modal mở
  useEffect(() => {
    const billData = localStorage.getItem("bill");
    if (billData) {
      setBill(JSON.parse(billData)); // Chuyển đổi dữ liệu JSON từ localStorage thành mảng
    }
  }, [isOpen]); // Dữ liệu sẽ được lấy lại khi modal được mở

  // Tính tổng số tiền
  const calculateTotal = () => {
    return bill.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onRequestClose}
      contentLabel="Order Bill"
      className="modal-content"
      overlayClassName="modal-overlay"
    >
      <div className="order-container">
        <div className="header">
          <span className="store-name">Order History</span>
        </div>
        <div className="order-details">
          <div className="product-info">
            {bill.length > 0 ? (
              bill.map((item) => (
                <div key={item.id} className="product-item">
                  <p className="product-title">{item.name}</p>
                  <p className="product-desc">
                    Ngày đặt hàng: {new Date(item.id).toLocaleString()}
                  </p>
                  <p className="product-size">Size: {item.size}</p>
                  <p className="product-quantity">Số lượng: {item.quantity}</p>
                  <p className="product-price">Giá: {item.price} VND</p>
                </div>
              ))
            ) : (
              <p>No orders found.</p>
            )}
          </div>
        </div>
        <div className="total-section">
          <p className="total-text">Tổng số tiền: {calculateTotal()} VND</p>
        </div>
      </div>
    </Modal>
  );
}
