import Modal from "react-modal";
import Pizza from "../Data/Pizza";
import "../Styles/Bill.css";

export default function OrderBill({ isOpen, onRequestClose }) {
  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onRequestClose}
      contentLabel="Order Bill"
      className="modal-content"
      overlayClassName="modal-overlay"
    >
      <div className="order-container">
        <div className="header">
          <span className="store-name">Order History</span>
        </div>

        {/* Order Details Section */}
        <div className="order-details">
          {Pizza.map((item, index) => (
            <div key={index} className="product-item">
              <div className="product-info">
                <p className="product-name">{item.name}</p>
                <p className="product-desc">Ngày đặt hàng: {item.orderDate}</p>
              </div>
              <div className="price-info">
                <p className="product-price">Giá: {item.price} VND</p>
                <p className="product-quantity">Số lượng: {item.quantity}</p>
                <p className="product-total">
                  Tổng tiền: {item.price * item.quantity} VND
                </p>
              </div>
            </div>
          ))}
        </div>

        {/* Total Price Section */}
        <div className="total-section">
          <p className="total-text">
            Tổng số tiền:{" "}
            {Pizza.reduce(
              (total, item) => total + item.price * item.quantity,
              0
            )}{" "}
            VND
          </p>
        </div>
      </div>
    </Modal>
  );
}
